package lukesh;

public class lukesh_for_loop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int i=0;i<5;i=i+1)
		{
			System.out.printf("hello %d \n ",i);
		} 
		for (int j=1;j<7;j++)
		{
			if (j==5)
			{
				System.out.println("hello boys how do you do with "+ j);
				
			}
			else 
			{
				System.out.println("good morning"+ j);
			}
		}
	}

}
